import style from './DetailsCard.module.css';
import { useDispatch, useSelector } from 'react-redux';
import {useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getGameID } from '../../Redux/Actions';
const DetailsCard = (params) => {
    const path = window.location.pathname;
    const idPath = path.split('/').pop();
    console.log(idPath)
    const dispatch = useDispatch()
    const { id } = useParams()
    console.log(id, params.id)
    const {name, image, plataform, description, released, rating} = useSelector(state => state.videogame);
    useEffect(() => {
        dispatch(getGameID(id));
      }, [dispatch, id]);
   
    if (!id) {
        return <div>Cargando detalles...</div>;
    };  
    console.log( image, plataform)
    return (
        
        <div className={style.container}>
            <div>
                <button className={style.btn} onClick={() => window.history.back()}>Back</button>
            </div>
            <div>
                <h3 className={style.title}>{name}</h3>
            </div>
            <div className={style.imgcontainer}>
                <img src={image} alt="Imagen Juego" />
            </div>
            <div>
                <h4>{plataform}</h4>
            </div>
            <div>
                {description}
            </div>
            <div>
                <p>{released}</p>
            </div>
            <div>
                <p>{rating}</p>
