import style from './Pagination.module.css'
import axios from 'axios';
import { useEffect, useState } from 'react';

export default function Pagination({ gamesPerPage, allGames, paginate }) {

  const [games, setGames] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`/videogames?page=${currentPage}`);
        setGames(response.data);
        // Actualizar el número total de páginas
        // (asumiendo que la respuesta contiene esta información)
        setTotalPages(response.totalPages);
      } catch (error) {
        console.error('Error fetching games:', error);
      }
    };

    fetchData();
  }, [currentPage]);

  return (

  <div className={style.container}>
    <button onClick={() => paginate()} className={style.btn}>Prev</button>
    <label htmlFor="" className={style.label}>{pageNum}</label>
    <button onClick={() => setCurrentPage(currentPage + 1)} disabled={currentPage === totalPages}>Next</button>
  </div>


  )
}
