import style from './CardGame.module.css';
import { NavLink } from 'react-router-dom';

export default function CardGame(param) {
    const { name, image, genre, id } = param
    return (
        
        <div className={style.containerCard}>
            <div className={style.imgcontainer}>
                <img className={style.img} src={image} alt={name} onClick={() => window.location.href = `/details/${param.id}`}/>
            </div>
            <div className={style.textcontainer}>
                <h3 className={style.title}>{name}</h3>
                <h3 className={style.title}>{genre}</h3>
                <NavLink
                        to={`/details/${id}`}
                        className={style.detailLink}
                    >More Details
                </NavLink>
            </div>
        </div>
        
    )
}
