import style from './DetailsCard.module.css';
import axios from 'axios';
import { useState, useEffect } from 'react';
const DetailsCard = ({ id }) => {
    const [cardDetails, setCardDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCardDetails = async () => {
      try {
        // Realizar una solicitud para obtener los detalles de la carta usando el ID
        const response = await axios.get(`http://localhost:3001/videogames/${id}`);
        // Actualizar el estado con los detalles de la carta
        setCardDetails(response.data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchCardDetails();

    // Limpia los efectos cuando el componente se desmonta
    return () => {
      setCardDetails(null);
      setLoading(true);
      setError(null);
    };
  }, [id]);

  if (loading) {
    return <div>Cargando detalles...</div>;
  }

  if (error) {
    return <div>Error al cargar detalles: {error}</div>;
  }

  if (!cardDetails) {
    return <div>No se encontraron detalles para esta carta.</div>;
  }


    return (
        <div className={style.container}>
            <div>
                <button className={style.btn} onClick={() => window.history.back()}>Back</button>
            </div>
            <div>
                <h3 className={style.title}>{cardDetails.name}</h3>
            </div>
            <div className={style.imgcontainer}>
                <img src={cardDetails.image} alt={cardDetails.name} />
            </div>
            <div>
                <h4>{cardDetails.plataform}</h4>
            </div>
            <div>
                <p>{cardDetails.description}</p>
            </div>
            <div>
                <p>{cardDetails.released}</p>
            </div>
            <div>
                <p>{cardDetails.rating}</p>
            </div>
            <div>
                <p>{cardDetails.genres.join(', ')}</p>
            </div>
        </div>
    )
}

export default DetailsCard
