import style from './Pagination.module.css'

export default function Pagination({ gamesPerPage, allGames, paginate }) {

    const pageNumbers = [];

    for (let i = 1; i <= Math.ceil(allGames / gamesPerPage); i++) {
        pageNumbers.push(i);
    }

    return (
        <nav className={style.containerOrder}>
            <ul className={style.input}>
                {
                    pageNumbers && pageNumbers.map(number => (
                        <li key={number} onClick={() => paginate(number)} className={style.input}>
                            <span className={style.input}>{number}</span>
                        </li>
                    ))
                }
            </ul>
        </nav>
    )
}
