import style from './Pagination.module.css'

export default function Pagination({ gamesPerPage, allGames, paginate }) {

    const pageNumbers = [];

    for (let i = 1; i <= Math.ceil(allGames / gamesPerPage); i++) {
        pageNumbers.push(i);
    }

    return 

    <div>
      <button onClick={() => paginate(1)}>Prev</button>
      <label htmlFor="">{pageNumbers}</label>
      <button onClick={() => paginate(2)}>Next</button>
    </div>
  
  
    
}
