import style from './FormsAddGames.module.css';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getAllGenres, getAllPlatforms, createGame } from '../../Redux/Actions';
import SuccessAddGame from '../SuccesAddGame/SuccesAddGame';

export default function FormsAddGames() {
    const dispatch = useDispatch();
    const allGenres = useSelector(state => state.genres);
    const allPlatforms = useSelector(state => state.platforms);

    useEffect(() => {
        dispatch(getAllGenres());
    }, [dispatch]);

    useEffect(() => {
        dispatch(getAllPlatforms());
    }, [dispatch]);

    const [formData, setFormData] = useState({
        name: '',
        image: '',
        description: '',
        platforms: [],
        released: '',
        rating: '',
        genres: [],
    });

    const [showConfirmation, setShowConfirmation] = useState(false);

    const handleChange = (event) => {
        const { name, value, type, checked } = event.target;
        if (type === 'checkbox') {
            setFormData((prevState) => {
                if (name === 'platforms') {
                    return {
                        ...prevState,
                        platforms: checked
                            ? [...prevState.platforms, value]
                            : prevState.platforms.filter((platform) => platform !== value),
                    };
                } else if (name === 'genres') {
                    return {
                        ...prevState,
                        genres: checked
                            ? [...prevState.genres, value]
                            : prevState.genres.filter((genre) => genre !== value),
                    };
                }
                return prevState;
            });
        } else {
            setFormData({
                ...formData,
                [name]: value,
            });
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            await dispatch(createGame(formData));
            setShowConfirmation(true);
        } catch (error) {
            console.error('Error al agregar el juego:', error);
        }
    };

    const handleCloseModal = () => {
        setShowConfirmation(false);
    };

    return (
        <div className={style.container}>
            <form onSubmit={handleSubmit} className={style.input}>
                <div className={style.input}>
                    <label htmlFor="name" className={style.input}>Name: </label>
                    <input type="text" name="name" id="name" placeholder="Name" required className={style.inputText} value={formData.name} onChange={handleChange} />
                </div>
                <div className={style.input}>
                    <label htmlFor="image" className={style.input}>Image: </label>
                    <input type="text" name="image" id="image" placeholder="Image" required className={style.inputText} value={formData.image} onChange={handleChange} />
                </div>
                <div className={style.input}>
                    <label htmlFor="description" className={style.input}>Description: </label>
                    <textarea className={style.inputText} name="description" id="description" placeholder="Description" required value={formData.description} onChange={handleChange} />
                </div>
                <div className={style.inputGenres}>
                    <label htmlFor="platforms" className={style.input}>Platforms: </label>
                    {allPlatforms.map((platform) => (
                        <div key={platform.id} className={style.genres}>
                            <span>{platform.name}</span>
                            <input type="checkbox" name="platforms" value={platform.name} onChange={handleChange} />
                        </div>
                    ))}
                </div>
                <div className={style.input}>
                    <label htmlFor="released" className={style.input}>Released: </label>
                    <input type="date" name="released" id="released" placeholder="Released" required className={style.inputText} value={formData.released} onChange={handleChange} />
                </div>
                <div className={style.input}>
                    <label htmlFor="rating" className={style.input}>Rating: </label>
                    <input type="number" name="rating" max={5} min={1} id="rating" placeholder="Rating" required className={style.inputText} value={formData.rating} onChange={handleChange} />
                </div>
                <div className={style.inputGenres}>
                    <label htmlFor="genres" className={style.input}>Genres: </label>
                    {allGenres.map((genre) => (
                        <div key={genre.id} className={style.genres}>
                            <span>{genre.name}</span>
                            <input type="checkbox" name="genres" value={genre.name} onChange={handleChange} />
                        </div>
                    ))}
                </div>
                <button type="submit" className={style.buttonS}>Submit</button>
            </form>
            <SuccessAddGame show={showConfirmation} onClose={handleCloseModal} />
        </div>
    );
}
