import style from './GamesCards.module.css';
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import CardGame from '../CardGame/CardGame';
import Pagination from '../Pagination/Pagination';
import { getAllGames } from '../../Redux/Actions';

export default function GamesCards() {
  const dispatch = useDispatch();
  const games = useSelector(state => state.games); // Accede al estado de los juegos desde Redux

  useEffect(() => {
    dispatch(getAllGames());
  }, [dispatch]);

  return (
    <div className={style.containerCards}>
       {/* Verificar si la lista de juegos está definida antes de mapearla */}
       {games && games.map(game => (
        <CardGame
          key={game.id} // Asegúrate de incluir una clave única para cada elemento en el array mapeado
          id={game.id}
          name={game.name}
          image={game.image}
          genre={game.genres.join(', ')}
        />
      ))}
    </div>
  );
}
