import style from './CardGame.module.css';
import { NavLink } from 'react-router-dom';
import { useDispatch } from 'react-redux';
export default function CardGame(param) {
    const dispatch = useDispatch()
    handleDispatch = () => {
        dispatch({type: 'ADD_TO_CART', payload: param.id})
    }
    return (        
        <div className={style.containerCard}>
            <div className={style.imgcontainer}>
                <img className={style.img} src={param.image} alt={param.name} onClick={() => window.location.href = `/details/${param.id}`}/>
            </div>
            <div className={style.textcontainer}>
                <h3 className={style.title}>{param.name}</h3>
                <h3 className={style.title}>{param.genre}</h3>
                <NavLink
                        to={`/details/${param.id}`}
                        className={style.detailLink}
                    >More Details
                </NavLink>
            </div>
        </div>
        
    )
}
