import { useEffect, useState } from 'react';
import axios from 'axios';
import style from './GamesCards.module.css';
import CardGame from '../CardGame/CardGame';
import Pagination from '../Pagination/Pagination';

export default function GamesCards() {
  const [games, setGames] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`/videogames?page=${currentPage}&pageSize=5`);
        setGames(response.data);
      } catch (error) {
        console.error('Error fetching games:', error);
      }
    };

    fetchData();
  }, [currentPage]);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className={style.containerCards}>
      {games.map((game) => (
        <CardGame key={game.id} name={game.name} image={game.background_image} />
      ))}
      <Pagination currentPage={currentPage} onPageChange={handlePageChange} totalPages={5} />
    </div>
  );
}
