import style from './DetailsCard.module.css';

const DetailsCard = ({ cardId }) => {
    const [cardDetails, setCardDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCardDetails = async () => {
      try {
        // Realizar una solicitud para obtener los detalles de la carta usando el ID
        const response = await axios.get(`API_URL/${cardId}`);
        // Actualizar el estado con los detalles de la carta
        setCardDetails(response.data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchCardDetails();

    // Limpia los efectos cuando el componente se desmonta
    return () => {
      setCardDetails(null);
      setLoading(true);
      setError(null);
    };
  }, [cardId]);

  if (loading) {
    return <div>Cargando detalles...</div>;
  }

  if (error) {
    return <div>Error al cargar detalles: {error}</div>;
  }

  if (!cardDetails) {
    return <div>No se encontraron detalles para esta carta.</div>;
  }


    return (
        <div className={style.container} id={param.key}>
            <div>
                <button className={style.btn} onClick={() => window.history.back()}>Back</button>
            </div>
            <div>
                <h3 className={style.title}>{param.name}</h3>
            </div>
            <div className={style.imgcontainer}>
                <img src={param.image} alt={param.name} />
            </div>
            <div>
                <h4>{param.plataform}</h4>
            </div>
            <div>
                <p>{param.description}</p>
            </div>
            <div>
                <p>{param.released}</p>
            </div>
            <div>
                <p>{param.rating}</p>
            </div>
            <div>
                <p>{param.genres.join(', ')}</p>
            </div>
        </div>
    )
}

export default DetailsCard
