import style from './FormsAddGames.module.css';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getAllGenres, getAllPlatforms, createGame } from '../../Redux/Actions';
import Modal from '../Modal/Modal';

export default function FormsAddGames() {
    const dispatch = useDispatch();
    const allGenres = useSelector(state => state.genres);
    const allPlatforms = useSelector(state => state.platforms);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(false);

    useEffect(() => {
        dispatch(getAllGenres());
    }, [dispatch]);

    useEffect(() => {
        dispatch(getAllPlatforms());
    }, [dispatch]);

    const [formData, setFormData] = useState({
        name: '',
        image: '',
        description: '',
        platforms: [],
        released: '',
        rating: '',
        genre: [],
    });

    const [isFormValid, setIsFormValid] = useState(false);

    useEffect(() => {
        const isValid = formData.name && formData.image && formData.description && formData.platforms.length > 0 && formData.released && formData.rating && formData.genre.length > 0;
        setIsFormValid(isValid);
    }, [formData]);

    const handleChange = (event) => {
        const { name, value, type, checked } = event.target;

        if (type === 'checkbox') {
            setFormData((prevData) => ({
                ...prevData,
                [name]: checked ? [...prevData[name], value] : prevData[name].filter((item) => item !== value),
            }));
        } else {
            setFormData({
                ...formData,
                [name]: value
            });
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        setLoading(true);
        setError(null);
        setSuccess(false);

        try {
            await dispatch(createGame(formData));
            setSuccess(true);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className={style.container}>
            <form onSubmit={handleSubmit} className={style.input}>
                <div className={style.input}>
                    <label htmlFor="name" className={style.input}>Name: </label>
                    <input type="text" name="name" id="name" value={formData.name} onChange={handleChange} placeholder="Name" required className={style.inputText} />
                </div>
                <div className={style.input}>
                    <label htmlFor="image" className={style.input}>Image: </label>
                    <input type="text" name="image" id="image" value={formData.image} onChange={handleChange} placeholder="Image" required className={style.inputText} />
                </div>
                <div className={style.input}>
                    <label htmlFor="description" className={style.input}>Description: </label>
                    <input className={style.inputText} type="textarea" name="description" id="description" value={formData.description} onChange={handleChange} placeholder="Description" required />
                </div>
                <div className={style.inputGenres}>
                    <label htmlFor="platforms" className={style.input}>Platforms: </label>
                    {allPlatforms.map((platform) => (
                        <div key={platform.id} className={style.genres}>
                            <span>{platform.name}</span>
                            <input type="checkbox" name="platforms" value={platform.name} onChange={handleChange} />
                        </div>
                    ))}
                </div>
                <div className={style.input}>
                    <label htmlFor="released" className={style.input}>Released: </label>
                    <input type="date" name="released" id="released" value={formData.released} onChange={handleChange} placeholder="Released" required className={style.inputText} />
                </div>
                <div className={style.input}>
                    <label htmlFor="rating" className={style.input}>Rating</label>
                    <input type="number" name="rating" max={5} min={1} id="rating" value={formData.rating} onChange={handleChange} placeholder="Rating" required className={style.inputText} />
                </div>
                <div className={style.inputGenres}>
                    <label htmlFor="genre" className={style.input}>Genre: </label>
                    {allGenres.map((genre) => (
                        <div key={genre.id} className={style.genres}>
                            <span>{genre.name}</span>
                            <input type="checkbox" name="genre" value={genre.name} onChange={handleChange} />
                        </div>
                    ))}
                </div>
                <button type="submit" className={style.buttonS} disabled={!isFormValid}>Submit</button>
            </form>
            <Modal loading={loading} error={error} success={success} />
        </div>
    );
}
