import { useEffect, useState } from 'react';
import axios from 'axios';
import style from './GamesCards.module.css';
import CardGame from '../CardGame/CardGame';



export default function GamesCards({ param }) {
  const [games, setGames] = useState([]);

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/videogames?page=${page}`);
        setGames(response.data);
      } catch (error) {
        console.error('Error fetching games:', error);
      }
    };

    fetchGames();
  }, [page]);

  return (
    <div className={style.container}>
      <div>
        <Link to={`/page/${page - 1}`}>Previous Page</Link>
        <h3>Page {page}</h3>
        <Link to={`/page/${page + 1}`}>Next Page</Link>
      </div>
      {
        games.map(param => (
          <CardGame key={param.id} name={param.name} image={param.image} genre={param.genre} />
        ))
      }
    </div>

  )
}
