import style from './Pagination.module.css'

export default function Pagination({ gamesPerPage, allGames, paginate }) {

  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(allGames / gamesPerPage); i++) {
    pageNumbers.push(i);
  }
  const page = pageNumbers[0];

  return (

  <div className={style.container}>
    <button onClick={() => paginate()} className={style.btn}>Prev</button>
    <label htmlFor="" className={style.label}>{paginate}</label>
    <button onClick={() => paginate()} className={style.btn}>Next</button>
  </div>


  )
}
