import style from './Pagination.module.css'

export default function Pagination({ gamesPerPage, allGames, paginate }) {

  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(allGames / gamesPerPage); i++) {
    pageNumbers.push(i);
  }

  return (

  <div className={style.container}>
    <button onClick={() => paginate(1)} className={style.btn}>Prev</button>
    <label htmlFor="" className={style.label}>{pageNumbers}</label>
    <button onClick={() => paginate(2)} className={style.btn}>Next</button>
  </div>


  )
}
